C++输出 <pre><code>#include&lt;iostream&gt;

using namespace std;

class A {
private:
    int x1;  // 完全是自己控制
public:
    int x2;  // 完全供大家控制
protected:   // 传给子类
    int x3;
public:
    A(int x1, int x2, int x3) : x1(x1), x2(x2), x3(x3) {
        cout &lt;&lt; x1 &lt;&lt; " " &lt;&lt; x2 &lt;&lt; " " &lt;&lt; x3 &lt;&lt; endl;
    }
    virtual ~A() {
        cout &lt;&lt; -x1 &lt;&lt; " " &lt;&lt; -x2 &lt;&lt; " " &lt;&lt; -x3 &lt;&lt; endl;
    }
};

class AA : public A {
    int xx;
public:
    AA(int x1, int x2, int x3, int xx) : A(x1, x2, x3), xx(xx) {
        cout &lt;&lt; x1 &lt;&lt; " " &lt;&lt; x2 &lt;&lt; " " &lt;&lt; x3 &lt;&lt; " " &lt;&lt; xx &lt;&lt; endl;
    }
    ~AA() {
        // cout &lt;&lt; -x1  不能存取父类中的私有成员
        cout &lt;&lt; -x2 &lt;&lt; " " &lt;&lt; -x3 &lt;&lt; " " &lt;&lt; -xx &lt;&lt; endl;
    }
};

int main() {
    A* p1 = new A(1, 2, 3);
    delete p1;

    AA* p2 = new AA(4, 5, 6, 7); // 此程序等价于A* p2 = new AA(4, 5, 6, 7);
    delete p2;

    return 0;
}
